import React from "react";
import ProductItem from "./ProductItem";

const ProductsGrid = ({ searchResults, categories }) => {
  return (
    <div id="productGrid">
      {searchResults.length === 0 ? (
        <p>查無商品</p>
      ) : (
        searchResults.map((result) => (
          <ProductItem
            key={result.id}
            product={result}
            categories={categories}
          />
        ))
      )}
    </div>
  );
};

export default ProductsGrid;
