import React from "react";

const Filter = ({ products, value, onChange }) => {
  const categories = [];
  products.forEach(function (item) {
    if (categories.indexOf(item.category) < 0) {
      categories.push(item.category);
    }
  });

  return (
    <div className="btn-group d-flex" role="group">
      <button
        className="btn btn-outline-secondary w-100 active"
        id="cat-button"
      >
        全部商品
      </button>
      {categories.map((category) => (
        <button
          key={category}
          className={
            category === value
              ? "btn btn-outline-secondary w-100 active"
              : "lbtn btn-outline-secondary w-100"
          }
          id="cat-button"
          onChange={onChange}
        >
          {category}
        </button>
      ))}
    </div>
  );
};

export default Filter;
