import React from "react";

const ProductItem = ({ product }) => {
  return (
    <div className="card card-body text-center">
      <img
        style={{
          display: "block",
          margin: "0 auto 10px",
          height: "200px",
        }}
        className="img-fluid"
        alt="prdImg"
        src={product.photo}
      />
      <p>{product.name}</p>
      {!product.xPrice && <h5 className="text-center"> $ {product.price} </h5>}
      {product.xPrice && (
        <div>
          <h5 className="strike "> $ {product.price} </h5>
          <h5 className="text-center"> 特價 $ {product.xPrice} </h5>
        </div>
      )}

      <button href="#" className="btn btn-outline-dark">
        Add to cart
      </button>
    </div>
  );
};

export default ProductItem;
