import React, { useContext } from "react";
import CartProducts from "./CartProducts";
import { CartContext } from "../contexts/CartContext";
import { Link } from "react-router-dom";

const Cart = () => {
  const { total, cartItems, itemCount, checkout, handleCheckout } = useContext(
    CartContext
  );
  var defaultCardViewStyle = {
    color: "rgb(0,0,0)",
    fontSize: "15px",
    lineHeight: "24px",
    fontWeight: "300",
    errorColor: "red",
    placeholderColor: "",
  };

  // isUsedCcv default true
  TPDirect.card.setup("#cardview-container", defaultCardViewStyle);

  // Display ccv field
  TPDirect.card.setup("#cardview-container", defaultCardViewStyle, {
    isUsedCcv: true,
  });

  // Not display ccv field
  TPDirect.card.setup("#cardview-container", defaultCardViewStyle, {
    isUsedCcv: false,
  });
  TPDirect.card.onUpdate(function (update) {
    if (update.canGetPrime) {
      submitButton.removeAttribute("disabled");
    } else {
      submitButton.setAttribute("disabled", true);
    }

    if (update.hasError) {
      cardViewContainer.classList.add("error");
    } else {
      cardViewContainer.classList.remove("error");
    }

    if (update.status.number) {
      showErrorMessage("Please check your credit card number");
    } else {
      hideErrorMessage();
    }
  });

  const handlePayment = () => {
    TPDirect.card.getPrime((result) => {
      if (result.status !== 0) {
        console.err("getPrime error");
      }
      var prime = result.card.prime;
      console.log("getPrime success: " + prime);
    });
  };

  return (
    <div>
      <div className="row no-gutters justify-content-center">
        <div className="col-sm-9 p-3">
          {cartItems.length > 0 ? (
            <CartProducts />
          ) : (
            <div className="p-3 text-center text-muted">
              購物車尚未添加任何商品
            </div>
          )}

          {checkout && (
            <div className="p-3 text-center text-success">
              <p>Checkout successfull</p>
              <Link to="/" className="btn btn-outline-success btn-sm">
                BUY MORE
              </Link>
            </div>
          )}
        </div>
        {cartItems.length > 0 && (
          <div className="col-sm-3 p-3">
            <div className="card card-body">
              <p className="mb-1">商品總數</p>
              <h4 className=" mb-3 txt-right">{itemCount}</h4>
              <p className="mb-1">總金額</p>
              <h3 className="m-0 txt-right">$ {total}</h3>
              <hr className="my-4" />
              <div className="text-center">
                <button onClick={handlePayment}>Pay</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cart;
