import React, { useContext } from "react";
import ProductItem from "./ProductItem";

import styles from "./ProductsGrid.module.scss";
import { ProductsContext } from "../contexts/ProductsContext";

const ProductsGrid = () => {
  const { products } = useContext(ProductsContext);

  //   var categories = new Set();
  //   array.forEach((products) => categories.add(products.category));
  //   categories = [...categories]; // Set to Array transformation

  //   console.log(categories); // ["shirt", "trousers"]
  var categories = [];
  products.forEach(function (item) {
    if (categories.indexOf(item.category) < 0) {
      categories.push(item.category);
    }
  });

  console.log(categories);

  return (
    <div className={styles.p__container} style={{ alignItems: "baseline" }}>
      <div className="row">
        <div className="col-sm-8" style={{ paddingTop: "20px" }}>
          <div className="btn-group d-flex" role="group">
            <button
              className="btn btn-outline-secondary w-100 active"
              id="cat-button"
            >
              {categories}
            </button>

            {/* <button className="btn btn-outline-secondary w-100" id="cat-button">
              {" "}
              生乳塔類{" "}
            </button>
            <button className="btn btn-outline-secondary w-100" id="cat-button">
              {" "}
              生乳捲類{" "}
            </button>
            <button className="btn btn-outline-secondary w-100" id="cat-button">
              {" "}
              波士頓蛋糕{" "}
            </button>
            <button className="btn btn-outline-secondary w-100" id="cat-button">
              {" "}
              節慶禮盒{" "}
            </button>
            <button className="btn btn-outline-secondary w-100" id="cat-button">
              {" "}
              其他商品{" "}
            </button> */}
          </div>
        </div>

        <div className="col-sm-4" style={{ paddingTop: "20px" }}>
          <div className="form-group">
            <input
              type="text"
              name=""
              placeholder="Search product"
              className="form-control"
              id="searchBar"
            />
          </div>
        </div>
      </div>
      <div className={styles.p__grid}>
        {products.map((product) => (
          <ProductItem key={product.id} product={product} />
        ))}
      </div>
      <div className={styles.p__footer}></div>
    </div>
  );
};

export default ProductsGrid;
