import React, { useContext } from "react";

import CartProducts from "./CartProducts";
import { CartContext } from "../contexts/CartContext";
import { Link } from "react-router-dom";

const ecpay_payment = require("../../node_modules/ECPAY_Payment_node_js/lib/ecpay_payment");

console.log(ecpay_payment);
//參數值為[PLEASE MODIFY]者，請在每次測試時給予獨特值
//若要測試非必帶參數請將base_param內註解的參數依需求取消註解 //
let base_param = {
  MerchantTradeNo: "f0a0d7e9fae1bb72bc93", //請帶20碼uid, ex: f0a0d7e9fae1bb72bc93
  MerchantTradeDate: "2017/02/13 15:45:30", //ex: 2017/02/13 15:45:30
  TotalAmount: "100",
  TradeDesc: "測試交易描述",
  ItemName: "測試商品等",
  ReturnURL: "http://192.168.0.1",
};

let create = new ecpay_payment();
let htm = create.payment_client.aio_check_out_credit_onetime(base_param);
console.log(htm);

const Cart = () => {
  const { total, cartItems, itemCount, checkout, handleCheckout } = useContext(
    CartContext
  );

  return (
    <div>
      <div className="row no-gutters justify-content-center">
        <div className="col-sm-9 p-3">
          {cartItems.length > 0 ? (
            <CartProducts />
          ) : (
            <div className="p-3 text-center text-muted">
              購物車尚未添加任何商品
            </div>
          )}

          {checkout && (
            <div className="p-3 text-center text-success">
              <p>Checkout successfull</p>
              <Link to="/" className="btn btn-outline-success btn-sm">
                BUY MORE
              </Link>
            </div>
          )}
        </div>
        {cartItems.length > 0 && (
          <div className="col-sm-3 p-3">
            <div className="card card-body">
              <p className="mb-1">商品總數</p>
              <h4 className=" mb-3 txt-right">{itemCount}</h4>
              <p className="mb-1">總金額</p>
              <h3 className="m-0 txt-right">$ {total}</h3>
              <hr className="my-4" />
              <div className="text-center">
                <button
                  type="button"
                  className="btn btn-success mb-2"
                  onClick={handleCheckout}
                >
                  結帳
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cart;
