import React, { useContext } from "react";
import firebase, { storage } from "../services/firebase";
import EditItem from "../components/EditItem";
import { AdminContext } from "../contexts/AdminContext";
import { CartContext } from "../contexts/CartContext";
import { Link } from "react-router-dom";

const ProductItem = ({ product, categories }) => {
  const { admin } = useContext(AdminContext);
  const { addProduct, cartItems, removeProduct } = useContext(CartContext);
  const isInCart = (product) => {
    return !!cartItems.find((item) => item.id === product.id);
  };
  const productId = product.id;
  const handleDelete = () => {
    firebase.firestore().collection("products").doc(productId).delete();
    let imageRef = storage.refFromURL(product.url);
    imageRef
      .delete()
      .then(() => {
        console.log("Deleted");
      })
      .catch((err) => console.log(err));
  };

  return (
    <div
      className="card card-body text-center"
      style={{ borderColor: "lightBlue" }}
    >
      {admin && (
        <div className="d-flex">
          <EditItem
            product={product}
            categories={categories}
            title="編輯商品"
          />
          <button
            className="btn btn-danger btn-sm mr-0 ml-auto"
            onClick={handleDelete}
          >
            X
          </button>
        </div>
      )}

      <img
        style={{
          display: "block",
          margin: "10px auto 10px",
          height: "200px",
        }}
        className="img-fluid"
        alt="prdImg"
        src={product.url}
      />
      <h6>{product.title}</h6>
      <div>
        <p>
          <a
            class="btn btn-primary"
            data-toggle="collapse"
            href="#collapseExample"
            role="button"
            aria-expanded="false"
            aria-controls="collapseExample"
          >
            Link with href
          </a>
        </p>
        <div class="collapse" id="collapseExample">
          <div class="card card-body">
            Anim pariatur cliche reprehenderit, enim eiusmod high life accusamus
            terry richardson ad squid. Nihil anim keffiyeh helvetica, craft beer
            labore wes anderson cred nesciunt sapiente ea proident.
          </div>
        </div>
      </div>
      {!product.xPrice && <h5 className="text-center"> $ {product.price} </h5>}
      {product.xPrice && (
        <div className="d-flex justify-content-center">
          <del className="strike " style={{ paddingRight: "20px" }}>
            {" "}
            $ {product.price}{" "}
          </del>
          <h5 className="text-center" style={{ color: "red" }}>
            {" "}
            特價 $ {product.xPrice}{" "}
          </h5>
        </div>
      )}

      {!isInCart(product) ? (
        <button
          className="btn btn-outline-success"
          onClick={() => addProduct(product)}
        >
          加入購物車
        </button>
      ) : (
        <button
          className="btn btn-outline-danger"
          onClick={() => removeProduct(product)}
        >
          移出購物車
        </button>
      )}
    </div>
  );
};

export default ProductItem;
