import React, { createContext, useState, useEffect } from "react";
import firebase, { auth } from "../services/firebase";

export const UserContext = createContext();

function UserAuthority() {
  const [authUser, setAuthUser] = useState();
  const [authority, setAuthority] = useState(false);

  useEffect(() => {
    const unsunscribe = auth.onAuthStateChanged((authUser) => {
      authUser ? setAuthUser(authUser.uid) : setAuthUser(null);
    });

    const userAuthority = firebase
      .firestore()
      .collection("users")
      .doc(authUser)
      .get()
      .then(function (doc) {
        if (doc.exists) {
          console.log("Document data:", doc.data().admin);
        } else {
          // doc.data() will be undefined in this case
          console.log("No such document!");
        }
      });

    return () => unsunscribe();
  }, []);
  return authUser;
}

const UserContextProvider = ({ children }) => {
  const authUser = UserAuthority();

  return (
    <UserContext.Provider value={{ authUser }}>{children}</UserContext.Provider>
  );
};

export default UserContextProvider;
