import React, { useContext } from "react";
import CartProducts from "./CartProducts";
import { CartContext } from "../contexts/CartContext";
import { Link } from "react-router-dom";
const express = require("express");
const ecpay_payment = require("../ecpay_payment_nodejs/index");

const app = express();
const port = 5000;

const makeid = (length) => {
  let result = "";
  const characters =
    "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
  const charactersLength = characters.length;
  for (var i = 0; i < length; i++) {
    result += characters.charAt(Math.floor(Math.random() * charactersLength));
  }
  return result;
};

app.get("/", (req, res) => {
  res.send("Hello World!");
});

app.get("/pay", (req, res) => {
  const base_param = {
    MerchantTradeNo: makeid(20),
    MerchantTradeDate: "2020/09/07 23:00:00",
    TotalAmount: "100",
    TradeDesc: "測試交易描述",
    ItemName: "測試商品",
    ReturnURL: "http://192.168.0.1",
  };

  const create = new ecpay_payment();
  const htm = create.payment_client.aio_check_out_credit_onetime(base_param);
  res.send(htm);
});

app.listen(port, () => {
  console.log(`Example app listening at http://localhost:${port}`);
});

const Cart = () => {
  const { total, cartItems, itemCount, checkout, handleCheckout } = useContext(
    CartContext
  );

  return (
    <div>
      <div
        className=" mt-3 pl-2 pr-2"
        style={{
          justifyContent: "center",
          backgroundColor: "white",
          alignItems: "center",
          marginLeft: "1rem",
          marginRight: "1rem",
        }}
      >
        <h1>購物車</h1>
      </div>

      <div className="row no-gutters justify-content-center">
        <div className="col-sm-9 p-3">
          {cartItems.length > 0 ? (
            <CartProducts />
          ) : (
            <div className="p-3 text-center text-muted">
              購物車尚未添加任何商品
            </div>
          )}

          {checkout && (
            <div className="p-3 text-center text-success">
              <p>Checkout successfull</p>
              <Link to="/" className="btn btn-outline-success btn-sm">
                BUY MORE
              </Link>
            </div>
          )}
        </div>
        {cartItems.length > 0 && (
          <div className="col-sm-3 p-3">
            <div className="card card-body">
              <p className="mb-1">商品總數</p>
              <h4 className=" mb-3 txt-right">{itemCount}</h4>
              <p className="mb-1">總金額</p>
              <h3 className="m-0 txt-right">$ {total}</h3>
              <hr className="my-4" />
              <div className="text-center">
                <button
                  target="_blank"
                  href="https://p-stage.ecpay.com.tw/400515E"
                  rel="noopener noreferrer"
                  onClick={handleCheckout}
                  style={{ border: "none" }}
                  type="button"
                  disabled
                >
                  <img
                    src="https://payment-stage.ecpay.com.tw/Content/themes/WebStyle20170517/images/ecgo.png"
                    alt=""
                    width="100%"
                  />
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cart;
