import React, { useState } from "react";
import CustomField from "../common/CustomField";
import { Formik, Form, Field, ErrorMessage } from "formik";
import BootForm from "react-bootstrap/Form";
import { Button } from "react-bootstrap";
import * as Yup from "yup";
import firebase, { storage } from "../services/firebase";

const EditForm = ({ categories, product, onCloseModal }) => {
  const [image, setImage] = useState(null);
  const [url, setUrl] = useState(product.url);
  const [progress, setProgress] = useState(0);
  const [title, setTitle] = useState(product.title);
  const [category, setCategory] = useState(product.category);
  const [price, setPrice] = useState(product.price);
  const [xPrice, setXPrice] = useState(product.xPrice);
  const productId = product.id;

  const handleImgDisplay = (event) => {
    setImage(event.target.files[0]);
  };

  const handleUpload = (e) => {
    e.preventDefault();
    const uploadTask = storage.ref(`images/${image.name}`).put(image);
    uploadTask.on(
      "state_changed",
      (snapshot) => {
        const progress = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        setProgress(progress);
      },
      (error) => {
        console.log(error);
      },
      () => {
        storage
          .ref("images")
          .child(image.name)
          .getDownloadURL()
          .then((url) => {
            setUrl(url);
          });
      }
    );
  };

  const handleSubmit = () => {
    if (title.trim() === "") {
      alert("?");
    } else {
      var editTime = Date();
      editTime = firebase.firestore.Timestamp.fromDate(new Date()).toDate();
      firebase
        .firestore()
        .collection("products")
        .doc(productId)
        .set({ title, category, price, xPrice, url, editTime })
        .then(onCloseModal);
    }
  };

  const validationSchema = Yup.object().shape({
    title: Yup.string().required("First Name is required"),
    category: Yup.string().required(),
    price: Yup.string().required("Password is required"),
    xPrice: Yup.string(),
  });

  const initialValues = {
    title: title,
    category: category,
    price: price,
    xPrice: xPrice,
  };

  return (
    <div>
      <section className="center" width="60%">
        <progress value={progress} max="100" />
        <BootForm className="ml-auto mr-auto">
          <img src={url} alt="productImg" height="200px" />
          <BootForm.File
            id="exampleFormControlFile1"
            label="選取圖片"
            onChange={handleImgDisplay}
          />
          <Button variant="danger" type="button" onClick={handleUpload}>
            新增
          </Button>
        </BootForm>
      </section>

      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={handleSubmit}
      >
        {({ errors, touched }) => (
          <Form>
            <CustomField
              label="產品名稱"
              name="title"
              errors={errors.title}
              touched={touched.title}
              onKeyUp={(e) => {
                setTitle(e.target.value);
              }}
            />
            <div className="form-group text-left">
              <label htmlFor="category">產品類別</label>
              <Field
                as="select"
                label="產品類別"
                name="category"
                onBlur={(e) => {
                  setCategory(e.target.value);
                }}
                className={
                  "form-control" +
                  (errors.category && touched.category ? " is-invalid" : "")
                }
              >
                {categories.map((category) => (
                  <option key={category} value={category}>
                    {category}
                  </option>
                ))}
              </Field>

              <ErrorMessage
                name="category"
                component="div"
                className="invalid-feedback"
              />
            </div>

            <CustomField
              label="定價"
              name="price"
              errors={errors.price}
              touched={touched.price}
              onKeyUp={(e) => {
                setPrice(e.target.value);
              }}
            />
            <CustomField
              label="特價"
              name="xPrice"
              placeholder="無特價請空白"
              errors={errors.xPrice}
              touched={touched.xPrice}
              onKeyUp={(e) => {
                setXPrice(e.target.value);
              }}
            />

            <Button variant="primary" type="submit" value="Submit" block>
              新增
            </Button>
          </Form>
        )}
      </Formik>
    </div>
    // <Form>
    //   <progress value={progress} max="100" />
    //   <Form.Group>
    //     <img
    //       src={url || "https://via.placeholder.com/300"}
    //       alt="productImg"
    //       height="200px"
    //     />
    //     <Form.File
    //       id="exampleFormControlFile1"
    //       label="選取圖片"
    //       onChange={handleImgDisplay}
    //     />
    //     <Button variant="danger" type="button" onClick={handleUpload}>
    //       新增
    //     </Button>
    //   </Form.Group>
    //   <Form.Group controlId="productTitle">
    //     <Form.Label>產品名稱</Form.Label>
    //     <Form.Control
    //       onChange={(e) => setTitle(e.currentTarget.value)}
    //       value={title}
    //     />
    //   </Form.Group>
    //   <Form.Group controlId="productCategory">
    //     <Form.Label>產品類別</Form.Label>
    //     <Form.Control
    //       value={category}
    //       as="select"
    //       onChange={(e) => setCategory(e.currentTarget.value)}
    //     >
    //       {categories.map((category) => (
    //         <option key={category}>{category}</option>
    //       ))}
    //     </Form.Control>
    //   </Form.Group>
    //   <Form.Group controlId="productPrice">
    //     <Form.Label>原價</Form.Label>
    //     <Form.Control
    //       value={price}
    //       type="number"
    //       onChange={(e) => setPrice(e.currentTarget.value)}
    //     />
    //   </Form.Group>
    //   <Form.Group controlId="productXPrice">
    //     <Form.Label>特價</Form.Label>
    //     <Form.Control
    //       value={xPrice}
    //       type="number"
    //       placeholder="無特價請空白"
    //       onChange={(e) => setXPrice(e.currentTarget.value)}
    //     />
    //   </Form.Group>
    //   <Button variant="primary" type="button" onClick={handleSubmit} block>
    //     確認變更
    //   </Button>
    // </Form>
  );
};

export default EditForm;
