import React, { createContext, useReducer } from "react";
import { CartReducer, sumItems } from "./CartReducer";
import firebase, { auth } from "../services/firebase";

export const CartContext = createContext();

const storage = localStorage.getItem("cart")
  ? JSON.parse(localStorage.getItem("cart"))
  : [];
const initialState = {
  cartItems: storage,
  ...sumItems(storage),
  checkout: false,
};

const CartContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(CartReducer, initialState);

  const increase = (payload) => {
    dispatch({ type: "INCREASE", payload });
  };

  const decrease = (payload) => {
    dispatch({ type: "DECREASE", payload });
  };

  const addProduct = (payload) => {
    dispatch({ type: "ADD_ITEM", payload });
  };

  const removeProduct = (payload) => {
    dispatch({ type: "REMOVE_ITEM", payload });
  };

  const clearCart = () => {
    dispatch({ type: "CLEAR" });
  };

  const handleCheckout = () => {
    console.log("CHECKOUT", state);
    const base_param = {
      MerchantTradeNo: makeid(20),
      MerchantTradeDate: "2020/09/07 23:00:00",
      TotalAmount: sumItems.total,
      TradeDesc: "測試交易描述",
      ItemName: "測試商品",
      ReturnURL: "http://192.168.0.1",
    };

    window.open("http://localhost:5000/pay");

    dispatch({ type: "CHECKOUT" });
    var consumer = "";
    if (auth.currentUser) {
      consumer = auth.currentUser.uid;
    }
    const purchaseDate = firebase.firestore.Timestamp.fromDate(
      new Date()
    ).toDate();
    firebase
      .firestore()
      .collection("consumption")
      .doc(purchaseDate.toString())
      .set({
        items: state,
        purchaseDate: purchaseDate,
        consumer: consumer,
      });
  };

  const contextValues = {
    removeProduct,
    addProduct,
    increase,
    decrease,
    clearCart,
    handleCheckout,
    ...state,
  };

  return (
    <CartContext.Provider value={contextValues}>
      {children}
    </CartContext.Provider>
  );
};

export default CartContextProvider;
