import React, { useState } from "react";
import { Button, Form } from "react-bootstrap";
import firebase, { storage } from "../services/firebase";

const ProductForm = ({ categories, onCloseModal }) => {
  const [image, setImage] = useState(null);
  const [url, setUrl] = useState(
    "https://scontent.ftpe12-1.fna.fbcdn.net/v/t31.0-0/p206x206/22712353_648259815562546_9040069549059309313_o.jpg?_nc_cat=104&_nc_sid=09cbfe&_nc_ohc=4RxqhClM2n0AX9KLJX_&_nc_ht=scontent.ftpe12-1.fna&_nc_tp=6&oh=c2710cb7a11d7c2f90527348f4d35902&oe=5F624FE0"
  );
  const [progress, setProgress] = useState(0);
  const [title, setTitle] = useState("");
  const [category, setCategory] = useState(categories[0]);
  const [price, setPrice] = useState("");
  const [xPrice, setXPrice] = useState("");

  const handleImgDisplay = (event) => {
    setImage(event.target.files[0]);
  };

  const handleUpload = (e) => {
    e.preventDefault();
    const uploadTask = storage.ref(`images/${image.name}`).put(image);
    uploadTask.on(
      "state_changed",
      (snapshot) => {
        const progress = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        setProgress(progress);
      },
      (error) => {
        console.log(error);
      },
      () => {
        storage
          .ref("images")
          .child(image.name)
          .getDownloadURL()
          .then((url) => {
            setUrl(url);
          });
      }
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (title.trim() === "") {
      alert("?");
    } else {
      const editTime = firebase.firestore.Timestamp.fromDate(
        new Date()
      ).toDate();
      firebase
        .firestore()
        .collection("products")
        .doc(editTime.toString())
        .set({
          title,
          category,
          price,
          xPrice,
          url,
          editTime,
        })
        .then(onCloseModal);
    }
  };

  return (
    <div id="form">
      <h1 id="formTitle">會員註冊</h1>
      <Formik
        initialValues={initialValues}
