import React, { Component } from "react";
import { Link } from "react-router-dom";
import DropdownMenu from "../common/DropdownMenu";
import logo from "../assets/logoWithText.png";
import NavItem from "../common/NavItem";

class NavBarMenu extends Component {
  state = {
    navCollapsed: true,
  };
  onToggleNav = () => {
    this.setState({ navCollapsed: this.state.navCollapsed });
  };

  render() {
    const { navCollapsed } = this.state;
    return (
      <nav
        id="navbar"
        className="navbar navbar-expand-lg navbar-light justify-content-between"
      >
        <Link className="navbar-brand" to="/home">
          <img src={logo} alt="bluxury-catering" width="250" height="60" />
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarNavDropdown"
          aria-controls="navbarNavDropdown"
          aria-expanded="false"
          aria-label="Toggle navigation"
          onClick={this.onToggleNav}
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div
          className={
            (navCollapsed ? "collapse " : "collapse show") + " navbar-collapse"
          }
          id="navbarNavDropdown"
        >
          <ul className="navbar-nav ml-auto " id="navbarItems">
            <NavItem title={"逆光記憶"} path={"逆光記憶"} />
            <NavItem title={"響粹"} path={"響粹"} />

            <DropdownMenu
              title={"品牌故事"}
              items={["藍穎餐飲", "逆光記憶", "響粹"]}
            />
            <DropdownMenu
              title={"安心食材"}
              items={["麵粉", "乳製品", "生鮮肉品", "生鮮蔬菜"]}
            />
            <NavItem
              title={
                <i
                  className="fa fa-shopping-cart"
                  aria-hidden="true"
                  style={({ cursor: "pointer" }, { fontSize: "40px" })}
                />
              }
              path={"購物車"}
            />
          </ul>
        </div>
      </nav>
    );
  }
}

export default NavBarMenu;
