const express = require("express");
const ecpay_payment = require("../../ecpay_payment_nodejs/index");

const app = express();
const port = 3000;
const React = require("react");
const renderToString = require("react-dom/server").renderToString;
const Home = require("../App").default;

const makeid = (length) => {
  let result = "";
  const characters =
    "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
  const charactersLength = characters.length;
  for (var i = 0; i < length; i++) {
    result += characters.charAt(Math.floor(Math.random() * charactersLength));
  }
  return result;
};

app.get("/", (req, res) => {
  const content = renderToString(<Home />);
  res.send(content);
});

app.get("/test", (req, res) => {
  const base_param = {
    MerchantTradeNo: makeid(20),
    MerchantTradeDate: "2020/09/07 23:00:00",
    TotalAmount: "100",
    TradeDesc: "測試交易描述",
    ItemName: "測試商品",
    ReturnURL: "http://192.168.0.1",
  };

  const create = new ecpay_payment();
  const htm = create.payment_client.aio_check_out_credit_onetime(
    (parameters = base_param)
  );
  res.send(htm);
});

app.listen(port, () => {
  console.log(`Example app listening at http://localhost:${port}`);
});
