import React, { useContext } from "react";
import SearchBox from "../common/SearchBox";
import Filter from "../common/Filter";
import ProductsGrid from "../common/ProductsGrid";
import { ProductsContext } from "../contexts/ProductsContext";
import styles from "../common/ProductsGrid.module.scss";

const Memory = () => {
  const { products } = useContext(ProductsContext);

  const [searchTerm, setSearchTerm] = React.useState("");
  const [selectedGenre, setSelectedGenre] = React.useState("全部商品");

  const [searchResults, setSearchResults] = React.useState([]);

  const categories = [];
  products.forEach(function (item) {
    if (categories.indexOf(item.category) < 0) {
      categories.push(item.category);
    }
  });
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
    console.log(e.target.value);
  };
  const handleItemCategoryChange = (cate) => {
    console.log(cate);
  };
  React.useEffect(() => {
    let results = products;
    if (searchTerm)
      results = products.filter((product) =>
        product.name.toLowerCase().includes(searchTerm)
      );
    else if (selectedGenre && selectedGenre.id)
      results = products.filter(
        (product) => product.category === selectedGenre._id
      );
    setSearchResults(results);
  }, [searchTerm]);

  return (
    <div className={styles.p__container} style={{ alignItems: "baseline" }}>
      <div className="row">
        <div className="col-sm-8" style={{ paddingTop: "20px" }}>
          <Filter
            categories={categories}
            selectedGenre={selectedGenre}
            onClick={handleItemCategoryChange}
          />
        </div>

        <div className="col-sm-4" style={{ paddingTop: "20px" }}>
          <SearchBox
            products={products}
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </div>
      </div>
      <ProductsGrid searchResults={searchResults} />
      <div className={styles.p__footer}></div>
    </div>
  );
};

export default Memory;
