import React, { useContext } from "react";
import ProductItem from "./ProductItem";
import SearchBox from "./SearchBox";
import Filter from "./Filter";

import styles from "./ProductsGrid.module.scss";
import { ProductsContext } from "../contexts/ProductsContext";

const ProductsGrid = ({ searchResults }) => {
  const { products } = useContext(ProductsContext);

  return (
    <div className={styles.p__container} style={{ alignItems: "baseline" }}>
      <div className="row">
        <div className="col-sm-8" style={{ paddingTop: "20px" }}>
          <Filter products={products} />
        </div>

        <div className="col-sm-4" style={{ paddingTop: "20px" }}>
          <SearchBox products={products} />
        </div>
      </div>
      <div className={styles.p__grid}>
        {searchResults.map((result) => (
          <ProductItem key={result.id} product={result} />
        ))}
      </div>
      <div className={styles.p__footer}></div>
    </div>
  );
};

export default ProductsGrid;
