import React, { useState } from "react";
import Joi from "joi-browser";

const RegisterForm = () => {
  const [email, setEmail] = useState();
  const [password, swtPassword] = useState();
  const [username, setUsername] = useState();
  const [data, setData] = useState([
    ["email"[""]],
    ["password"[""]],
    ["username"[""]],
  ]);
  const [errors, setErrors] = useState();

  const schema = {
    email: Joi.string().email().required().label("Username"),
    paspasswordsword: Joi.string().min(5).required().label("Password"),
    username: Joi.string().required().label("Name"),
  };

  const validate = () => {
    const options = { abortEarly: false };
    const errors = Joi.validate(data, schema, options);
    if (!errors) return null;

    return errors;
  };

  const validateProperty = ({ name, value }) => {
    const obj = { [name]: value };

    const { error } = Joi.validate(obj, schema);
    return error ? error.details[0].message : null;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const errors = validate();
    setErrors({ errors: errors || {} });
    if (errors) return;
    this.doSubmit();
  };

  const handleChange = ({ currentTarget: input }) => {
    const errorMessage = validateProperty(input);
    if (errorMessage) errors[input.name] = errorMessage;
    else delete errors[input.name];

    data[input.name] = input.value;
    this.setState({ data, errors });
  };

  const renderButton = (label) => {
    return (
      <button disabled={validate()} className="btn btn-primary mb-3">
        {label}
      </button>
    );
  };
  const renderInput = (name, label, type = "text") => {
    return (
      <div className="form-group ml-4 mr-4 text-left">
        <label htmlFor={name}>{label}</label>
        <input
          name={name}
          id={name}
          className="form-control"
          onChange={handleChange()}
        />
        {errors && <div className="alert alert-danger">{errors}</div>}
      </div>
    );
  };

  return (
    <div id="form">
      <section>
        <h1 className="h1">Register</h1>
        <form onSubmit={handleSubmit}>
          {renderInput("username", "Username")}
          {renderInput("password", "Password", "password")}
          {renderInput("name", "Name")}
          {renderButton("Register")}
        </form>
      </section>
    </div>
  );
};

export default RegisterForm;
