import React from "react";
import firebase, { storage } from "../services/firebase";

import EditItem from "../components/EditItem";

const ProductItem = ({ product, categories, user }) => {
  const productId = product.id;
  const handleDelete = () => {
    firebase.firestore().collection("products").doc(productId).delete();
    let imageRef = storage.refFromURL(product.url);
    imageRef
      .delete()
      .then(() => {
        console.log("Deleted");
      })
      .catch((err) => console.log(err));
  };

  return (
    <div className="card card-body text-center">
      {user && (
        <div className="d-flex">
          <EditItem
            product={product}
            productId={productId}
            categories={categories}
          />
          <button
            className="btn btn-danger btn-sm mr-0 ml-auto"
            onClick={handleDelete}
          >
            X
          </button>
        </div>
      )}

      <img
        style={{
          display: "block",
          margin: "10px auto 10px",
          height: "200px",
        }}
        className="img-fluid"
        alt="prdImg"
        src={product.url}
      />
      <h6>{product.title}</h6>
      {!product.xPrice && <h5 className="text-center"> $ {product.price} </h5>}
      {product.xPrice && (
        <div className="d-flex justify-content-center">
          <del className="strike " style={{ paddingRight: "20px" }}>
            {" "}
            $ {product.price}{" "}
          </del>
          <h5 className="text-center" style={{ color: "red" }}>
            {" "}
            特價 $ {product.xPrice}{" "}
          </h5>
        </div>
      )}

      <button href="#" className="btn btn-outline-dark">
        加入購物車
      </button>
    </div>
  );
};

export default ProductItem;
