import React, { useState } from "react";
import { Button, Form } from "react-bootstrap";
import firebase from "../services/firebase";
import Joi from "joi-browser";

const ProductForm = ({ categories, onSubmit }) => {
  const [product, setProduct] = useState([]);
  const [image, setImage] = useState(null);
  const [title, setTitle] = useState(null);
  const [category, setCategory] = useState(categories[0]);
  const [price, setPrice] = useState(null);
  const [xPrice, setXPrice] = useState(null);

  const schema = Joi.object().keys({
    title: Joi.string().required(),
    category: Joi.string().required(),
  });

  validate = () => {
    const options = { abortEarly: false };
    const { error } = Joi.validate(product, schema, options);
    if (!error) return null;

    const errors = {};
    for (let item of error.details) errors[item.path[0]] = item.message;
    return errors;
  };

  validateProperty = ({ name, value }) => {
    const obj = { [name]: value };
    const schema = { [name]: schema[name] };
    const { error } = Joi.validate(obj, schema);
    return error ? error.details[0].message : null;
  };

  const handleImgUpload = (event) => {
    setImage(URL.createObjectURL(event.target.files[0]));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    validate(title);
    if (title.trim().length() === 0) {
      alert("?");
    } else {
      firebase
        .firestore()
        .collection("products")
        .add({ title, category, price, xPrice });
    }
  };

  return (
    <Form onSubmit={onSubmit}>
      <Form.Group>
        <img
          src={image || "https://via.placeholder.com/300"}
          alt="productImg"
          height="200px"
        />
        <Form.File
          id="exampleFormControlFile1"
          label="選取圖片"
          onChange={handleImgUpload}
        />
      </Form.Group>
      <Form.Group controlId="productTitle">
        <Form.Label>產品名稱</Form.Label>
        <Form.Control
          onChange={(e) => setTitle(e.currentTarget.value)}
          require="true"
        />
      </Form.Group>
      <Form.Group controlId="productCategory">
        <Form.Label>產品類別</Form.Label>
        <Form.Control
          as="select"
          value={category}
          onChange={(e) => setCategory(e.currentTarget.value)}
        >
          {categories.map((category) => (
            <option key={category}>{category}</option>
          ))}
        </Form.Control>
      </Form.Group>
      <Form.Group controlId="productPrice">
        <Form.Label>原價</Form.Label>
        <Form.Control
          type="number"
          onChange={(e) => setPrice(e.currentTarget.value)}
        />
      </Form.Group>
      <Form.Group controlId="productXPrice">
        <Form.Label>特價</Form.Label>
        <Form.Control
          type="number"
          placeholder="無特價請空白"
          onChange={(e) => setXPrice(e.currentTarget.value)}
        />
      </Form.Group>
      <Button variant="primary" type="button" onClick={handleSubmit} block>
        新增
      </Button>
    </Form>
  );
};

export default ProductForm;
