import React, { useState } from "react";
import { Button, Form } from "react-bootstrap";
import firebase from "../services/firebase";

const ProductForm = ({ categories }) => {
  const [image, setImage] = useState(null);
  const [title, setTitle] = useState("");
  const [category, setCategory] = useState(categories[0]);
  const [price, setPrice] = useState(0);
  const [xPrice, setXPrice] = useState(null);
  const handleImgUpload = (event) => {
    setImage(URL.createObjectURL(event.target.files[0]));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    firebase
      .firestore()
      .collection("products")
      .add({ title, category, price, xPrice });
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group>
        <img
          src={image || "https://via.placeholder.com/300"}
          alt="productImg"
          height="200px"
        />
        <Form.File
          id="exampleFormControlFile1"
          label="選取圖片"
          onChange={handleImgUpload}
        />
      </Form.Group>
      <Form.Group controlId="productTitle">
        <Form.Label>產品名稱</Form.Label>
        <Form.Control
          value={title}
          onChange={(e) => setTitle(e.currentTarget.value)}
        />
      </Form.Group>
      <Form.Group controlId="productCategory">
        <Form.Label>產品類別</Form.Label>
        <Form.Control
          as="select"
          value={category}
          onChange={(e) => setCategory(e.currentTarget.value)}
        >
          {categories.map((category) => (
            <option key={category}>{category}</option>
          ))}
        </Form.Control>
      </Form.Group>
      <Form.Group controlId="productPrice">
        <Form.Label>原價</Form.Label>
        <Form.Control
          type="number"
          onChange={(e) => setPrice(e.currentTarget.value)}
        />
      </Form.Group>
      <Form.Group controlId="productXPrice">
        <Form.Label>特價</Form.Label>
        <Form.Control
          type="number"
          placeholder="無特價請空白"
          onChange={(e) => setXPrice(e.currentTarget.value)}
        />
      </Form.Group>
      <Button variant="primary" type="submit" block>
        新增
      </Button>
    </Form>
  );
};

export default ProductForm;
