import React, { createContext, useState, useEffect } from "react";
import firebase, { auth } from "../services/firebase";

export const UserContext = createContext();

function UserAuthority() {
  const [authUser, setAuthUser] = useState("null");
  const [authority, setAuthority] = useState(false);

  useEffect(() => {
    const unsunscribe = auth.onAuthStateChanged((authUser) => {
      authUser ? setAuthUser(authUser.uid) : setAuthUser(null);
    });

    return () => unsunscribe();
  }, []);
  return authUser;
}

const UserContextProvider = ({ children }) => {
  const authUser = UserAuthority();

  return (
    <UserContext.Provider value={{ authUser }}>{children}</UserContext.Provider>
  );
};

export default UserContextProvider;
