import { useState, useEffect } from "react";
import { auth } from "../services/firebase";

const useFetchUser = () => {
  const [authUser, setAuthUser] = useState(null);

  useEffect(() => {
    auth.onAuthStateChanged((user) => {
      if (user) {
        setAuthUser(user.uid);
      } else {
        setAuthUser(null);
      }
    });
  });
  return authUser;
};

export default useFetchUser;
