import React, { useContext } from "react";
import firebase, { storage } from "../services/firebase";
import EditItem from "../components/EditItem";
import { AdminContext } from "../contexts/AdminContext";
import { CartContext } from "../contexts/CartContext";
import {
  PlusCircleIcon,
  MinusCircleIcon,
  TrashIcon,
} from "../assets/icons/icon";

const ProductItem = ({ product, categories }) => {
  const { admin } = useContext(AdminContext);
  const {
    addProduct,
    cartItems,
    increase,
    decrease,
    removeProduct,
  } = useContext(CartContext);
  const isInCart = (product) => {
    return !!cartItems.find((item) => item.id === product.id);
  };
  const productId = product.id;
  const handleDelete = () => {
    firebase.firestore().collection("products").doc(productId).delete();
    let imageRef = storage.refFromURL(product.url);
    imageRef
      .delete()
      .then(() => {
        console.log("Deleted");
      })
      .catch((err) => console.log(err));
  };

  return (
    <div className="card card-body text-center">
      {admin && (
        <div className="d-flex">
          <EditItem product={product} categories={categories} />
          <button
            className="btn btn-danger btn-sm mr-0 ml-auto"
            onClick={handleDelete}
          >
            X
          </button>
        </div>
      )}

      <img
        style={{
          display: "block",
          margin: "10px auto 10px",
          height: "200px",
        }}
        className="img-fluid"
        alt="prdImg"
        src={product.url}
      />
      <h6>{product.title}</h6>
      {!product.xPrice && <h5 className="text-center"> $ {product.price} </h5>}
      {product.xPrice && (
        <div className="d-flex justify-content-center">
          <del className="strike " style={{ paddingRight: "20px" }}>
            {" "}
            $ {product.price}{" "}
          </del>
          <h5 className="text-center" style={{ color: "red" }}>
            {" "}
            特價 $ {product.xPrice}{" "}
          </h5>
        </div>
      )}

      {!isInCart(product) ? (
        <button
          className="btn btn-outline-dark"
          onClick={() => addProduct(product)}
        >
          加入購物車
        </button>
      ) : (
        <div className=" p-2 text-right d-flex justify-content-between">
          <button
            onClick={() => decrease(product)}
            className="btn btn-danger btn-sm mr-2 mb-1"
          >
            <MinusCircleIcon width={"20px"} />
          </button>
          <p className="mb-0">{product.quantity}</p>
          <button
            onClick={() => increase(product)}
            className="btn btn-primary btn-sm mr-2 mb-1"
          >
            <PlusCircleIcon width={"20px"} />
          </button>
        </div>
      )}
    </div>
  );
};

export default ProductItem;
